#perform extended euclidian algorithm to find the d value for RSA
def extended_euclidian(a,b):
    prev_x = 1
    prev_y = 0
    x = 1
    y = 1
    a_orig = a

    if( a > b):
        while b > 0:
            q = a // b
            a, b = b, a % b
            x, prev_x = prev_x - q*x, x
            y, prev_y = prev_y - q*y, y
    
        if prev_y < 0:
            return (prev_y + a_orig)

        return prev_y
    else:
        return extended_euclidian(b,a)

